function heroSlider(){if(!document.querySelector(".hero"))return null;new Swiper(".hero__slider",{pagination:{el:".hero__slider-pagination",type:"fraction"},navigation:{nextEl:".hero__slider-next",prevEl:".hero__slider-prev"},direction:"horizontal",slidesPerView:1,spaceBetween:0,pagination:{el:".hero__slider-pagination",type:"custom",clickable:!0,renderCustom:function(e,r,i){function t(e){return e<10?"0"+e.toString():e.toString()}return t(r)+"<span>/"+t(i)+"</span>"}}})}function reviewsSlider(){if(!document.querySelector(".reviews"))return null;new Swiper(".reviews__slider",{slidesPerView:2,spaceBetween:40,navigation:{nextEl:".reviews__slider-next",prevEl:".reviews__slider-prev"}})}function burgerMenu(){const e=document.querySelector(".burger"),r=document.querySelector(".menu"),i=document.querySelector("body");e.addEventListener("click",(()=>{r.classList.contains("active")?(r.classList.remove("active"),e.classList.remove("active-burger"),i.classList.remove("locked")):(r.classList.add("active"),e.classList.add("active-burger"),i.classList.add("locked"))}));document.querySelectorAll(".menu__item").forEach((t=>{t.addEventListener("click",(function(){r.classList.remove("active"),e.classList.remove("active-burger"),i.classList.remove("locked")}))})),window.addEventListener("resize",(()=>{window.innerWidth>991.98&&(r.classList.remove("active"),e.classList.remove("active-burger"),i.classList.remove("locked"))}))}function fixedNav(){const e=document.querySelector("nav");window.scrollY>=1?e.classList.add("fixed__nav"):e.classList.remove("fixed__nav")}heroSlider(),reviewsSlider(),burgerMenu(),window.addEventListener("scroll",fixedNav);const modal=new GraphModal;function enabledInput(){document.querySelectorAll(".input").forEach((e=>{value=e.value.length,value>""||value>0?e.classList.add("enabled"):e.classList.remove("enabled")}))}enabledInput();